class ModalController{constructor(a=""){this.size=a,this.modalName="modal-default",this.startConfig()}startConfig(){let a=$("#"+this.modalName+" .modal-dialog");a.removeClass("modal-sm modal-lg modal-fluid").addClass(this.size),$("#"+this.modalName+"modal-default-footer").find("button:not(.close-btn)").remove();let b=$("#"+this.modalName+"-body");return b.empty(),this}setModalName(a){return this.modalName=a,this.startConfig(),this}show(){return $("#"+this.modalName+"-header").children().fadeOut(),$("#"+this.modalName).modal(),this}hide(){return $("#"+this.modalName).modal("toggle"),this}appendBody(a){"div";let b=$("#"+this.modalName+"-body");return b.append(a),this}appendButton(a){let b=$("#"+this.modalName+"-footer");return b.prepend(a),this}clearButtons(){return $("#"+this.modalName+"-footer").find("button:not(.close-btn)").remove(),this}}
